      <title>Consórcio Canopus</title>
      <style id="divi-style-inline-inline-css" type="text/css"> a,abbr,acronym,address,applet,b,big,blockquote,body,center,cite,code,dd,del,dfn,div,dl,dt,em,fieldset,font,form,h1,h2,h3,h4,h5,h6,html,i,iframe,img,ins,kbd,label,legend,li,object,ol,p,pre,q,s,samp,small,span,strike,strong,sub,sup,tt,u,ul,var{
         margin:0;
         padding:0;
         border:0;
         outline:0;
         font-size:100%;
         -ms-text-size-adjust:100%;
         -webkit-text-size-adjust:100%;
         vertical-align:baseline;
         background:transparent
     }
     body{
         line-height:1
     }
     ol,ul{
         list-style:none
     }
     blockquote,q{
         quotes:none
     }
     blockquote:after,blockquote:before,q:after,q:before{
         content:"";
         content:none
     }
     blockquote{
         margin:20px 0 30px;
         border-left:5px solid;
         padding-left:20px
     }
     :focus{
         outline:0
     }
     del{
         text-decoration:line-through
     }
     pre{
         overflow:auto;
         padding:10px
     }
     figure{
         margin:0
     }
     table{
         border-collapse:collapse;
         border-spacing:0
     }
     article,aside,footer,header,hgroup,nav,section{
         display:block
     }
     body{
         font-family:Open Sans,Arial,sans-serif;
         font-size:14px;
         color:#666;
         background-color:#fff;
         line-height:1.7em;
         font-weight:500;
         -webkit-font-smoothing:antialiased;
         -moz-osx-font-smoothing:grayscale
     }
     body.page-template-page-template-blank-php #page-container{
         padding-top:0!important
     }
     body.et_cover_background{
         background-size:cover!important;
         background-position:top!important;
         background-repeat:no-repeat!important;
         background-attachment:fixed
     }
     a{
         color:#2ea3f2
     }
     a,a:hover{
         text-decoration:none
     }
     p{
         padding-bottom:1em
     }
     p:not(.has-background):last-of-type{
         padding-bottom:0
     }
     p.et_normal_padding{
         padding-bottom:1em
     }
     strong{
         font-weight:700
     }
     cite,em,i{
         font-style:italic
     }
     code,pre{
         font-family:Courier New,monospace;
         margin-bottom:10px
     }
     ins{
         text-decoration:none
     }
     sub,sup{
         height:0;
         line-height:1;
         position:relative;
         vertical-align:baseline
     }
     sup{
         bottom:.8em
     }
     sub{
         top:.3em
     }
     dl{
         margin:0 0 1.5em
     }
     dl dt{
         font-weight:700
     }
     dd{
         margin-left:1.5em
     }
     blockquote p{
         padding-bottom:0
     }
     embed,iframe,object,video{
         max-width:100%
     }
     h1,h2,h3,h4,h5,h6{
         color:#333;
         padding-bottom:10px;
         line-height:1em;
         font-weight:500
     }
     h1 a,h2 a,h3 a,h4 a,h5 a,h6 a{
         color:inherit
     }
     h1{
         font-size:30px
     }
     h2{
         font-size:26px
     }
     h3{
         font-size:22px
     }
     h4{
         font-size:18px
     }
     h5{
         font-size:16px
     }
     h6{
         font-size:14px
     }
     input{
         -webkit-appearance:none
     }
     input[type=checkbox]{
         -webkit-appearance:checkbox
     }
     input[type=radio]{
         -webkit-appearance:radio
     }
     input.text,input.title,input[type=email],input[type=password],input[type=tel],input[type=text],select,textarea{
         background-color:#fff;
         border:1px solid #bbb;
         padding:2px;
         color:#4e4e4e
     }
     input.text:focus,input.title:focus,input[type=text]:focus,select:focus,textarea:focus{
         border-color:#2d3940;
         color:#3e3e3e
     }
     input.text,input.title,input[type=text],select,textarea{
         margin:0
     }
     textarea{
         padding:4px
     }
     button,input,select,textarea{
         font-family:inherit
     }
     img{
         max-width:100%;
         height:auto
     }
     .clear{
         clear:both
     }
     br.clear{
         margin:0;
         padding:0
     }
     .pagination{
         clear:both
     }
     #et_search_icon:hover,.et-social-icon a:hover,.et_password_protected_form .et_submit_button,.form-submit .et_pb_buttontton.alt.disabled,.nav-single a,.posted_in a{
         color:#2ea3f2
     }
     .et-search-form,blockquote{
         border-color:#2ea3f2
     }
     #main-content{
         background-color:#fff
     }
     .container{
         width:80%;
         max-width:1080px;
         margin:auto;
         position:relative
     }
     body:not(.et-tb) #main-content .container,body:not(.et-tb-has-header) #main-content .container{
         padding-top:58px
     }
     .et_full_width_page #main-content .container:before{
         display:none
     }
     .main_title{
         margin-bottom:20px
     }
     .et_password_protected_form .et_submit_button:hover,.form-submit .et_pb_button:hover{
         background:rgba(0,0,0,.05)
     }
     .et_button_icon_visible .et_pb_button{
         padding-right:2em;
         padding-left:.7em
     }
     .et_button_icon_visible .et_pb_button:after{
         opacity:1;
         margin-left:0
     }
     .et_button_left .et_pb_button:hover:after{
         left:.15em
     }
     .et_button_left .et_pb_button:after{
         margin-left:0;
         left:1em
     }
     .et_button_icon_visible.et_button_left .et_pb_button,.et_button_left .et_pb_button:hover,.et_button_left .et_pb_module .et_pb_button:hover{
         padding-left:2em;
         padding-right:.7em
     }
     .et_button_icon_visible.et_button_left .et_pb_button:after,.et_button_left .et_pb_button:hover:after{
         left:.15em
     }
     .et_password_protected_form .et_submit_button:hover,.form-submit .et_pb_button:hover{
         padding:.3em 1em
     }
     .et_button_no_icon .et_pb_button:after{
         display:none
     }
     .et_button_no_icon.et_button_icon_visible.et_button_left .et_pb_button,.et_button_no_icon.et_button_left .et_pb_button:hover,.et_button_no_icon .et_pb_button,.et_button_no_icon .et_pb_button:hover{
         padding:.3em 1em!important
     }
     .et_button_custom_icon .et_pb_button:after{
         line-height:1.7em
     }
     .et_button_custom_icon.et_button_icon_visible .et_pb_button:after,.et_button_custom_icon .et_pb_button:hover:after{
         margin-left:.3em
     }
     #left-area .post_format-post-format-gallery .wp-block-gallery:first-of-type{
         padding:0;
         margin-bottom:-16px
     }
     .entry-content table:not(.variations){
         border:1px solid #eee;
         margin:0 0 15px;
         text-align:left;
         width:100%
     }
